import assert from 'assert';
import ParitiesInstance from '../core/convert/ParitiesInstance.js';
import SignsInstance from '../core/convert/SignsInstance.js';
import KeysInstance from '../core/convert/KeysInstance.js';
import solve from './solve.js';

/**
 * Decide whether the given instance has a satisfying assignment.
 *
 * @param {ParitiesInstance|SignsInstance|KeysInstance} instance
 * @return {boolean} Whether the instance has a satisfying assignment.
 */
export default function decide(instance) {
	assert(
		instance instanceof ParitiesInstance ||
			instance instanceof SignsInstance ||
			instance instanceof KeysInstance,
	);
	return !solve(instance).next().done;
}

import ParitiesInstance from '../core/convert/ParitiesInstance.js';
import _sign_to_parity from '../core/convert/_sign_to_parity.js';
import _keys_to_parity from '../core/convert/_keys_to_parity.js';
import _parse_DIMACS_CNF from '../core/parse/_parse_DIMACS_CNF.js';

/**
 * The input is converted to parity format in each case.
 */
const from = {
	/**
	 * The input is in parity format where variables are given integer labels
	 * 0 to n-1, a positive literal is represented by the double of its
	 * variable's label, and a negative literal is represented by the successor
	 * of the double of its variable's label.
	 *
	 * @param {number[][]} clauses The clauses in parity format.
	 * @return {ParitiesInstance}
	 */
	parities: (clauses) => new ParitiesInstance(clauses),
	/**
	 * The input is in signs format where variables are given integer labels
	 * 1 to n, a positive literal is represented by its variable's label, and a
	 * negative literal is represented by the opposite of its variable's label.
	 *
	 * @function
	 * @param {number[][]} clauses
	 * @return {SignsInstance}
	 */
	signs: _sign_to_parity,
	/**
	 * The input is in keys format where variables are given arbitrary object labels,
	 * a literal is represented by a pair from the set {true, false} x
	 * labels, where the first item in the pair is true if the literal is positive,
	 * and false otherwise.
	 *
	 * @function
	 * @param {[any,any][][]} clauses
	 * @return {KeysInstance}
	 */
	keys: _keys_to_parity,
	/**
	 * Parses a character iterable in DIMACS CNF format.
	 *
	 * @param {Iterable<string>} iterable
	 * @return {ParitiesInstance}
	 */
	dcnf: (iterable) => new ParitiesInstance(_parse_DIMACS_CNF(iterable)),
};

export default from;

import setup_assignment from '../core/setup_assignment.js';
import setup_watchlist from '../core/SAT0W/setup_watchlist.js';
import SAT0W from '../core/SAT0W/SAT0W.js';

/**
 * Yields all satisfying assignments for the input instance.
 *
 * @param {ParitiesInstance|SignsInstance|KeysInstance} instance
 * @returns {IterableIterator<number[]>} Generator of the satisfying assignments.
 */
export default function solve({clauses, n}) {
	// eslint-disable-next-line new-cap
	return SAT0W(n, clauses, setup_watchlist(n, clauses), setup_assignment(n), 0);
}

import _verify from '../core/_verify.js';
const verify = ({clauses}, certificate) => _verify(clauses, certificate);
export default verify;

import update_watchlist from './update_watchlist.js';

/**
 * SAT0W.
 *
 * Recursively solve SAT by assigning to variables d, d+1, ..., n-1. Assumes
 * variables 0, ..., d-1 are assigned so far. A generator for all the
 * satisfying assignments is returned.
 *
 * @param {number} n
 * @param {number[][]} clauses
 * @param {number[][][]} watchlist
 * @param {number[]} assignment
 * @param {number} d
 * @return {IterableIterator<number[]>} Yields satisfying assignments.
 */
export default function* SAT0W(n, clauses, watchlist, assignment, d) {
	if (d === n) {
		yield assignment;
		return;
	}

	for (const a of [0, 1]) {
		assignment[d] = a;

		if (update_watchlist(watchlist, (d << 1) | a, assignment)) {
			// eslint-disable-next-line new-cap
			yield* SAT0W(n, clauses, watchlist, assignment, d + 1);
		}
	}

	assignment[d] = -1;
}

/**
 * Setup_watchlist.
 *
 * @param {number} n
 * @param {number[][]} clauses
 * @return {number[][][]} The literal watch list.
 */
export default function setup_watchlist(n, clauses) {
	const watchlist = [];

	for (let i = 0; i < 2 * n; ++i) watchlist.push([]);

	for (const clause of clauses) {
		// # Make the clause watch its first literal
		watchlist[clause[0]].push(clause);
	}

	return watchlist;
}

/**
 * Updates the watch list after literal 'false_literal' was just assigned
 * False, by making any clause watching false_literal watch something else.
 * Returns False it is impossible to do so, meaning a clause is contradicted
 * by the current assignment.
 *
 * @param {number[][][]} watchlist
 * @param {number} false_literal
 * @param {number[]} assignment
 */
export default function update_watchlist(watchlist, false_literal, assignment) {
	while (watchlist[false_literal].length > 0) {
		const clause =
			watchlist[false_literal][watchlist[false_literal].length - 1];
		let found_alternative = false;

		for (const alternative of clause) {
			const v = alternative >>> 1;
			const a = alternative & 1;

			if (assignment[v] === -1 || (assignment[v] === a) ^ 1) {
				found_alternative = true;
				watchlist[false_literal].pop();
				watchlist[alternative].push(clause);
				break;
			}
		}

		if (!found_alternative) return false;
	}

	return true;
}

/**
 * Verifies that an assignment vector satisfies a k-CNF formula given as a list
 * of clauses. The list of clauses and the assignment vector are in parity
 * format.
 *
 * @param {Iterable} clauses The clauses in parity format.
 * @param {Array} assignment The assignment vector in parity format.
 * @returns {Boolean} Whether the assignment satisfies the k-CNF formula
 * represented by the list of clauses.
 */
export default function _verify(clauses, assignment) {
	// eslint-disable-next-line no-labels
	loop: for (const clause of clauses) {
		for (const literal of clause) {
			// eslint-disable-next-line no-labels
			if ((literal ^ assignment[literal >>> 1]) & 1) continue loop;
		}

		return false;
	}

	return true;
}

import _certificate_to_keys from './_certificate_to_keys.js';

export default class KeysInstance {
	/**
	 * Constructor.
	 *
	 * Clauses are in parity format. The variables parameter allows to map
	 * variable indices to variable names.
	 *
	 * @param {any[]} variables The variables of this instance.
	 * @param {number[][]} clauses The clauses in parity format.
	 */
	constructor(variables, clauses) {
		/** @member {any[]} The variables of this instance. */
		this.variables = variables;
		/** @member {number[][]} The clauses in parity format. */
		this.clauses = clauses;
		/** @member {number} The number of variables in this instance. */
		this.n = this.variables.length;
	}

	/**
	 * Returns an object that maps each variable name to a variable assignment.
	 *
	 * @param {number[]} certificate
	 * @return {Map<any, number>}
	 */
	assignment(certificate) {
		return _certificate_to_keys(this.variables, certificate);
	}
}

import _count from './_count.js';

export default class ParitiesInstance {
	/**
	 * Clauses are in parity format.
	 *
	 * @param {number[][]} clauses The clauses in parity format.
	 */
	constructor(clauses) {
		/** @member {number[][]} The clauses in parity format. */
		this.clauses = clauses;
		/** @member {number} The number of variables in this instance. */
		this.n = _count(clauses);
	}

	/**
	 * Returns assignment vector in parity format.
	 *
	 * @param {number[]} certificate
	 * @return {number[]}
	 */
	assignment(certificate) {
		return certificate.slice();
	}
}

import _count from './_count.js';

export default class SignsInstance {
	/**
	 * Clauses are in parity format.
	 *
	 * @param {number[][]} clauses The clauses in parity format.
	 */
	constructor(clauses) {
		/** @member {number[][]} The clauses in parity format. */
		this.clauses = clauses;
		/** @member {number} The number of variables in this instance. */
		this.n = _count(clauses);
	}

	/**
	 * Returns assignment vector in signs format.
	 *
	 * @param {number[]} certificate
	 * @return {number[]}
	 */
	assignment(certificate) {
		return [0].concat(certificate);
	}
}

import {_zip2} from '@aureooms/js-itertools';

/**
 * Constructs an assignment map from a list of variable names and an assignment
 * vector in parity format.
 *
 * @param {any[]} variables
 * @param {number[]} certificate
 * @return {Map<any, number>}
 */
export default function _certificate_to_keys(variables, certificate) {
	return new Map(_zip2(variables, certificate));
}

/**
 * Counts the number of variables in a parity format clauses list.
 *
 * @param {number[][]} clauses The clauses list in parity format.
 * @returns {number} The number of variables in the clauses list.
 */
export default function _count(clauses) {
	let c = -1;

	for (const clause of clauses) {
		for (const literal of clause) {
			c = Math.max(c, literal >>> 1);
		}
	}

	return c + 1;
}

import KeysInstance from './KeysInstance.js';

/**
 * _keys_to_parity.
 *
 * @param {[any,any][][]} clauses
 * @return {KeysInstance}
 */
export default function _keys_to_parity(clauses) {
	const keys = new Map();
	const variables = [];

	const instance = [];

	for (const clause of clauses) {
		const new_clause = new Set();

		for (const [positive, variable] of clause) {
			if (!keys.has(variable)) {
				keys.set(variable, variables.length);
				variables.push(variable);
			}

			const negated = positive ? 0 : 1;
			const literal = (keys.get(variable) << 1) | negated;

			new_clause.add(literal);
		}

		instance.push([...new_clause]);
	}

	return new KeysInstance(variables, instance);
}

import {list, map} from '@aureooms/js-itertools';

/**
 * _parity_to_keys.
 *
 * @param {any[]} variables
 * @param {number[][]} clauses
 */
export default function _parity_to_keys(variables, clauses) {
	return list(
		map(
			(clause) =>
				list(
					map((literal) => [!(literal & 1), variables[literal >>> 1]], clause),
				),
			clauses,
		),
	);
}

import {list, map} from '@aureooms/js-itertools';

/**
 * _parity_to_sign.
 *
 * @param {number[][]} clauses
 * @return {number[][]}
 */
export default function _parity_to_sign(clauses) {
	return list(
		map(
			(clause) =>
				list(
					map(
						(literal) =>
							literal & 1 ? -(literal >>> 1) - 1 : (literal >>> 1) + 1,
						clause,
					),
				),
			clauses,
		),
	);
}

import {list, map} from '@aureooms/js-itertools';

import SignsInstance from './SignsInstance.js';

/**
 * _sign_to_parity.
 *
 * @param {number[][]} clauses
 * @return {SignsInstance}
 */
export default function _sign_to_parity(clauses) {
	return new SignsInstance(
		list(
			map(
				(clause) =>
					list(
						map(
							(literal) =>
								literal > 0 ? (literal - 1) << 1 : ((-literal - 1) << 1) | 1,
							clause,
						),
					),
				clauses,
			),
		),
	);
}

import _skip_blanks from './_skip_blanks.js';
import _parse_int from './_parse_int.js';

/**
 * _parse_DIMACS_CNF.
 *
 * @param {Iterable<string>} iterable
 */
export default function _parse_DIMACS_CNF(iterable) {
	const iterator = iterable[Symbol.iterator]();

	const clauses = [];

	// Skip comments
	while (true) {
		const current = iterator.next();

		if (current.done) return clauses;

		const c = current.value;

		// Go to problem description
		if (c === 'p') break;

		// Skip comment
		while (true) {
			const current = iterator.next();

			if (current.done) return clauses;

			// End of comment
			if (current.value === '\n') break;
		}
	}

	// Parse problem description
	// ( we do not actually need to parse it if we suppose it is correct )

	while (true) {
		const current = iterator.next();

		if (current.done) return clauses;

		// End of description
		if (current.value === '\n') break;
	}

	// Parse problem

	// eslint-disable-next-line no-labels
	loop: while (true) {
		const clause = [];

		while (true) {
			const [done1, symbol] = _skip_blanks(iterator);
			if (done1) {
				if (clause.length > 0) clauses.push(clause);
				return clauses;
			}

			const [done2, i] = _parse_int(symbol, iterator);
			if (i === 0) {
				if (done2) return clauses;
				clauses.push(clause);
				// eslint-disable-next-line no-labels
				continue loop;
			}

			clause.push(i > 0 ? (i - 1) << 1 : ((-i - 1) << 1) | 1);

			if (done2) {
				clauses.push(clause);
				return clauses;
			}
		}
	}
}

/**
 * _parse_int.
 *
 * @param {string} first_symbol
 * @param {Iterator<string>} iterator
 * @return {[boolean, number]}
 */
export default function _parse_int(first_symbol, iterator) {
	let i = 0;
	let s = 1;

	if (first_symbol === '-') s = -1;
	else i = Number(first_symbol);

	while (true) {
		const current = iterator.next();
		if (current.done) return [true, s * i];

		const c = current.value;

		if (c < '0' || c > '9') return [false, s * i];

		i *= 10;
		i += Number(c);
	}
}

/**
 * _skip_blanks.
 *
 * @param {Iterator<string>} iterator
 * @return {[boolean, string]}
 */
export default function _skip_blanks(iterator) {
	while (true) {
		const current = iterator.next();
		if (current.done) return [true, null];

		switch (current.value) {
			case ' ':
			case '\t':
			case '\n':
				continue;
			// HANDLE CUSTOM DELIMITER OF SATLIB
			case '%':
				return [true, null];
			default:
				return [false, current.value];
		}
	}
}

/**
 * Setup_assignment.
 *
 * @param {number} n
 * @return {number[]}
 */
export default function setup_assignment(n) {
	const assignment = new Array(n);

	for (let i = 0; i < n; ++i) assignment[i] = -1;

	return assignment;
}

